package myjava;

public class whilecondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * initialization while(condition){ statement increment/decrement }
		 */

		int a1 = 2;
		while (a1 <= 4) {
			System.out.println(a1);
			a1++;

		}
//print 0 to 6
		int x = 0;
		while (x <= 6) {
			System.out.println(x);
			x++;
		}
//table of 3
		int a2 = 3;
		while (a2 <= 30) {
			System.out.println(a2);
			a2 = a2 + 3;
		}

// table of 4 in reverse

		int a3 = 40;
		while (a3 >= 4) {
			System.out.println(a3);
			a3 = a3 - 4;
		}
		int d = 5;
		while (d <= 50) {
			System.out.println(d);
			d = d + 5;
		}
int d1 = 1;
while(d1 <= 5) {
	System.out.println(d1);
	d1 ++;
}
	}

}
